import React, { Component } from "react";
import "./App.css";
//componentes
import Formulario from "./components/Formulario";
import Resumen from "./components/Resumen";

import { obtenerDiferenciaAnio, calcularMarca, obtenerPlan } from "./Helper";

export default class App extends Component {

  state = {
    datosAuto: {},
    resultado: ""
  };

  contizarSeguro = datos => {
    const { marca, year, plan } = datos;
    let resultado = 2000;
    //obtener difenencia de año
    const diferenciaA = obtenerDiferenciaAnio(year);
    resultado -= (diferenciaA * 3 * resultado) / 300;
    resultado = calcularMarca(marca) * resultado;
    let increPlan = obtenerPlan(plan);

    resultado *= increPlan;
    resultado = parseFloat(resultado).toFixed(2);

    const datosAuto = {
      marca: marca,
      plan: plan,
      year: year

    };

    this.setState({
      datosAuto: datosAuto,
      resultado: resultado
    })
    console.log(this.state)
  }

  render() {
    return (
      <div className="App">
        <div className="contener-formulario">

          <Formulario
          cotizarSeguro {...this.cotizarSeguro}
          />

          <Resumen
             datosAuto={this.state.datosAuto}
           />

        </div>
      </div>
    );
  }
}
