import React, { Component } from 'react';

export default class Resultado extends Component {
    render() {
        const resultado = this.props.resultado
        const msg = (!msg)
            ? 'Eliga marca , año y plan'
            : 'El total es: $'
        return (
            <div className="resultado">
                <h3>{msg}</h3>
                <h3>{resultado}</h3>
            </div>
        )
    }
}
