import React, { Component } from "react";
import "./App.css";
//componentes
import Formulario from "./components/Formulario";
import Resumen from "./components/Resumen";
import Hello from "./components/Hello"

import { obtenerDiferenciaAnio, calcularMarca, obtenerPlan } from "./Helper";

export default class App extends Component {
  constructor(){
    super();
    this.state = {
      datosAuto: {},
      resultado: ""
    }
  }
  /*state = {

  };
*/
  contizarSeguro = datos => {
    const { marca, year, plan } = datos;
    let resultado = 2000;
    //obtener difenencia de año
    const difernciaA = obtenerDiferenciaAnio(year);
    resultado -= (difernciaA * 3 * resultado) / 300;
    resultado = calcularMarca(marca) * resultado;
    let increPlan = obtenerPlan(plan);

    resultado *= increPlan;
    resultado = parseFloat(resultado).toFixed(2);

    const datosAuto = {
      marca: marca,
      plan: plan,
      year: year

    };

    this.setState({
      datosAuto: datosAuto,
      resultado: resultado
    });

    console.log("Probando console log")
    console.log(datosAuto)
    console.log(resultado)

  };
  render() {
    return (
      <div className="App">
        <div className="contener-formulario">
          <Formulario cotizarSeguro {...this.cotizarSeguro} />
          <Resumen datosAuto={this.state.datosAuto} />

          <Hello/>

        </div>
      </div>
    );
  }
}
