import React, { Component } from "react";
import "./App.css";
//componentes
import Formulario from "./components/Formulario";
import Resumen from "./components/Resumen";
import Resultado from "./components/Resultado"

import { obtenerDiferenciaAnio, calcularMarca, obtenerPlan } from "./Helper";

export default class App extends Component {

  state = {
    datosAuto: {},
    resultado: ""
  };

  segueroCoti = (datos) => {
    //destructuring para los datos
    const { marca, year, plan } = datos
    //base del seguro 
    let resultado = 2000
    //obtener diferencia por a単o
    const diferenciaYear = obtenerDiferenciaAnio(year);
    //restar 3% por cada a単o que pasa
    resultado -= ((diferenciaYear * 3) * resultado) / 100
    //aumentar precio segun la marca
    resultado = calcularMarca(marca) * resultado
    //incrementar valor segun el plan 
    let incrementoPlan = obtenerPlan(plan)
    resultado *= incrementoPlan
    resultado = parseFloat(resultado).toFixed(2)

    const datosAuto = {
      marca: marca,
      year: year,
      plan: plan
    }

    this.setState({
      datosAuto: datosAuto,
      resultado: resultado,
    });
  }

  render() {
    return (
      <div className="App">
        <div className="contener-formulario">

          <Formulario
            cotizarSeguro {...this.segueroCoti}
          />

          <Resumen

          />

          <Resultado resultado ={this.state.resultado}/>

        </div>
      </div>
    );
  }
}
